import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { environment } from 'src/environments/environment';
import { lastValueFrom } from 'rxjs';
import { Ob }

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private textApiUrl = `http://localhost:5171/api/Text`
  private docxApiUrl = `${environment.apiUrl}/Docx`

  constructor(private http: HttpClient) { }

  postPastedText(text: string): Observable<any> {
    const payload = { text: text }; // Ensure the payload is an object with the 'text' property
    return this.http.post<any>(this.textApiUrl, payload, { headers: { 'Content-Type': 'application/json' } });
  }

  async postDocxFile(file: File): Promise<string> {
    try {
      const formData: FormData = new FormData();
      formData.append('file', file);

      const result = await lastValueFrom(this.http.post<string>(`${this.docxApiUrl}/docx`, formData));
      return result;
    } catch (error) {
      console.log('Error in processing docx', error);
      throw error;
    }
  }
}
