import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environments/environment';
import { firstValueFrom } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiService {
  private textApiUrl = `${environment.apiUrl}/text`
  private docxApiUrl = `${environment.apiUrl}/docx`

  constructor(private http: HttpClient) { }

  async postPastedText(text: string): Promise<string> {
    try {
      return await firstValueFrom(this.http.post<string>(`${this.textApiUrl}/text`, text));
    } catch (error) {
      console.log('Error', error);
      throw error;
    }
  }

  async postDocxFile(file: File): Promise<string> {
    try {
      const formData: FormData = new FormData();
      formData.append('file', file);

      return await firstValueFrom(this.http.post<string>(`${this.docxApiUrl}/docx`, formData));
    } catch (error) {
      console.log('Error in processing docx', error);
      throw error;
    }
  }
}
